/*****************************************************************************
 * FUNCTION
 *  wgui_virtual_keyboard_init
 * DESCRIPTION
 *  init virtual keyboard object
 * PARAMETERS
 *  void
 * RETURNS
 *  void
 * REMARKS
 *  This func used to confirm the MAX area of key can be cached
 *****************************************************************************/
static void wgui_virtual_keyboard_init(void)
{
    /*----------------------------------------------------------------*/
    /* Local Variables                                                */
    /*----------------------------------------------------------------*/
    S32 key_index;
    S32 max_value = 0, tmp;
    wgui_virtual_keyboard_layout_struct_p layout;
    gui_virtual_keyboard_key_struct_p key_list;
    U16 layout_count = 0; /* total layout count */
    U16 i = 0;

    /*----------------------------------------------------------------*/
    /* Code Body                                                      */
    /*----------------------------------------------------------------*/
	layout_count = sizeof(wgui_virtual_keyboard_layout_list) /
                   sizeof(wgui_virtual_keyboard_layout_struct_p);

    /* map language and layout */
    for (i = 0; i < layout_count; i++)
    {
        layout = wgui_virtual_keyboard_layout_list[i];
            key_list = layout->key_list;

            for (key_index = 0; key_index < layout->count; key_index++)
            {
                tmp = key_list[key_index].height * key_list[key_index].width;
                if (tmp > max_value)
                {
                    max_value = tmp;
                }
            }
        }

    MMI_DBG_ASSERT((((max_value * gdi_layer_get_bit_per_pixel()) + 7) >> 3) <=
                       GUI_VIRTUAL_KEYBOARD_CELL_CACHE_SIZE);
}


