/*****************************************************************************
 * FUNCTION
 *  wgui_virtual_keyboard_init_layout
 * DESCRIPTION
 *  Init virtual keyboard's layout, map language and layout/rotate-layout
 *   and sort language by gui_virtual_keyboard_language_enum value
 * PARAMETERS
 *  void
 * RETURNS
 *  void
 *****************************************************************************/
void wgui_virtual_keyboard_init_layout(void)
{
    /*----------------------------------------------------------------*/
    /* Local Variables                                                */
    /*----------------------------------------------------------------*/
    U16 layout_count = 0; /* total layout count */
    U16 key_count = 0; /* count of languange's key */
    U16 i;
    wgui_virtual_keyboard_layout_struct_p layout;

    /*----------------------------------------------------------------*/
    /* Code Body                                                      */
    /*----------------------------------------------------------------*/
    layout_count = sizeof(wgui_virtual_keyboard_layout_list) /
                   sizeof(wgui_virtual_keyboard_layout_struct_p);

    /* get the layouts for data check */
    for (i = 0; i < layout_count; i++)
    {
        layout = wgui_virtual_keyboard_layout_list[i];

        /* wgui_virtual_keyboard_layout_list[i].lang should be one value
         * of gui_virtual_keyboard_language_enum
         */
        MMI_DBG_ASSERT(layout->lang >= GUI_VIRTUAL_KEYBOARD_ENGLISH &&
                       layout->lang <= GUI_VIRTUAL_KEYBOARD_MAX_LANG);

        /* verify key count of tool output */
        if (layout->key_list != NULL)
        {
            key_count = 0;
			while (layout->key_list[key_count].content > 0)
            {
                key_count++;
            }
            MMI_DBG_ASSERT(key_count == layout->count);
        }
        else
        {
            MMI_DBG_ASSERT(layout->count == 0);
        }
    }

    wgui_virtual_keyboard_init();
}

