void wgui_virtual_keyboard_get_vk_rect(gui_virtual_keyboard_rect_struct_p rect,MMI_BOOL is_large)
{
    gui_virtual_keyboard_rect_struct ret_rect;
    U32 i,count;
    U32 left = 0, right = 0, bottom = 0;
    S32 key_x1,key_y1,key_width,key_height;
    S32 left_move_width = 0,right_move_width = 0,bottom_move_height = 0;
#ifdef __MMI_IME_HANDWRITING_OVER_VK__
    rect->x = g_wgui_virtual_keyboard.x;
    rect->y = g_wgui_virtual_keyboard.y;
    rect->width = g_wgui_virtual_keyboard.width;
    rect->height= g_wgui_virtual_keyboard.height;
#endif
    #ifdef __MMI_IME_VK_TYPE_HANDWRITING__
    memset(rect,0,sizeof(gui_virtual_keyboard_rect_struct));
    if(g_wgui_virtual_keyboard.vk_attribute != 3 && g_wgui_virtual_keyboard.vk_attribute != 11)
        return ;
    

    if(g_wgui_virtual_keyboard_multi_block_mode == MMI_FALSE)
    {

            ret_rect.x = MMI_content_x;//g_wgui_virtual_keyboard.x;, 
            ret_rect.y = UI_device_height - (g_wgui_virtual_keyboard.height + VK_BOTTOM_GAP);//g_wgui_virtual_keyboard.y;
            ret_rect.width = g_wgui_virtual_keyboard.width;
            ret_rect.height= g_wgui_virtual_keyboard.height;
            count = gui_virtual_keyboard_get_button_count(&g_wgui_virtual_keyboard);
            for(i = 0; i < count; i++)
            {
                    wgui_virtual_keyboard_get_button_rect(i,&key_x1,&key_y1,&key_width,&key_height);
                    if((key_x1 - ret_rect.x) < (key_width >> 2))
                        {
                            left++;
                            if(key_width > left_move_width)
                            {
                                left_move_width = key_width;
                            }
                        }
                    
                    if((ret_rect.width - (key_x1 + key_width))< (key_width >> 2))
                        {
                            right++;
                            if(key_width > right_move_width)
                            {
                                right_move_width = key_width;
                            }
    
                        }
    
                    if((ret_rect.height - (key_y1 + key_height)) < (key_height >> 2))
                        {
                            bottom++;
                            if(key_width > bottom_move_height)
                                {
                                    bottom_move_height = key_height;
                                }
                        }
                }
                if(left > 1)
                    {
                        ret_rect.x += left_move_width;
                    }
                if(right > 1)
                    {
                        if(left > 1)
                            ret_rect.width -= (right_move_width + left_move_width);
                        else
                        ret_rect.width -= right_move_width;
                    }
                if(bottom > 2)
                    {
                        ret_rect.height -= bottom_move_height;
            }
            rect->x = ret_rect.x;
            rect->y = ret_rect.y;
            rect->width = ret_rect.width;
            rect->height= ret_rect.height;
    }
    #endif
}

