/*****************************************************************************
 * FUNCTION
 *  wgui_virtual_keyboard_get_keymapping_from_layout
 * DESCRIPTION
 *  The function get the keymapping from keyboard's layout 
 * PARAMETERS
 *  layout       [IN]        Is the virtual keyboard pointer     
 *  len          [IN]        length of the data.
 *  key_mapping  [OUT]       group letter for keyboard.
 * RETURNS
 *  void
 *****************************************************************************/
static U32 wgui_virtual_keyboard_get_keymapping_from_layout(
    const wgui_virtual_keyboard_layout_struct* layout, 
    UI_character_type* key_mapping, 
    S32 len)
{
    /*----------------------------------------------------------------*/
    /* Local Variables                                                */
    /*----------------------------------------------------------------*/
    const wgui_virtual_keyboard_layout_struct* mini_keyboard_layout = NULL;
    int i,j,k,l;
    U32 key_code;
    UI_character_type *index;
    UI_character_type key_unicode;
    gui_virtual_keyboard_pen_enum key_event;
    U32 ret_val = 0;
    /*----------------------------------------------------------------*/
    /* Code Body                                                      */
    /*----------------------------------------------------------------*/
    if(layout == NULL)
    {
        return ret_val;
    }
    index = key_mapping;
    for(i = 0; i < layout->row_num; i++)
    {
        for(j = 0; j < layout->row_list[i].key_count; j++)
        {
            key_code = layout->row_list[i].key_list[j].code;
            key_event = (gui_virtual_keyboard_pen_enum) (key_code > 0xffff ? key_code : GUI_VKBD_PEN_NONE);
            key_unicode = key_code > 0xffff ? 0 : key_code;
            if(key_event == GUI_VKBD_PEN_DEFAULT_VALUE)
            {
                MMI_ASSERT(layout->row_list[i].key_list[j].label_string != NULL);
                key_unicode = layout->row_list[i].key_list[j].label_string[0];
            }           
            if(key_unicode > 0)
            {
                *index = key_unicode;
                index++;
                mini_keyboard_layout = (wgui_virtual_keyboard_layout_struct*) layout->row_list[i].key_list[j].popup_vk;

                if(mini_keyboard_layout != NULL)
                {
                    for(l = 0; l < mini_keyboard_layout->row_num; l++)
                    {
                        for(k = 0; k < mini_keyboard_layout->row_list[l].key_count; k++)
                        {
                            key_code = mini_keyboard_layout->row_list[l].key_list[k].code;
                            key_unicode = key_code > 0xffff ? 0 : key_code;
                            key_event = (gui_virtual_keyboard_pen_enum) (key_code > 0xffff ? key_code : GUI_VKBD_PEN_NONE);
                            if(key_event == GUI_VKBD_PEN_DEFAULT_VALUE)
                            {
                                MMI_ASSERT(mini_keyboard_layout->row_list[l].key_list[k].label_string != NULL);
                                key_unicode = mini_keyboard_layout->row_list[l].key_list[k].label_string[0];
                            }
                            if((key_unicode > '9' || key_unicode < '0') && (key_unicode != 0))
                            {
                                *index = key_unicode;
                                index++;
                                MMI_ASSERT((index - key_mapping) < len);
                            }
                        }
                    }
                }
                *index = 0;
                index++;
            }
            MMI_ASSERT((index - key_mapping) < len);
        }
    }
    ret_val = index - key_mapping;
    return ret_val;
}

void wgui_virtual_keyboard_get_qwerty_keymapping(UI_character_type* key_mapping, U32 len)
{
    /*----------------------------------------------------------------*/
    /* Local Variables                                                */
    /*----------------------------------------------------------------*/
    const wgui_virtual_keyboard_layout_struct* layout = NULL;
    U32 length;
    gui_virtual_keyboard_language_enum lang = GUI_VIRTUAL_KEYBOARD_MAX_LANG;

    /*----------------------------------------------------------------*/
    /* Code Body                                                      */
    /*----------------------------------------------------------------*/
    layout = wgui_virtual_keyboard_get_vk_layout(g_wgui_virtual_keyboard_lang_type, MMI_VK_TYPE_QWERTY);
    memset(key_mapping, 0, sizeof(UI_character_type)*len);

    length = wgui_virtual_keyboard_get_keymapping_from_layout(layout, key_mapping, len);
    switch(g_wgui_virtual_keyboard_lang_type)
    {
    case GUI_VIRTUAL_KEYBOARD_THAI:
        lang = GUI_VIRTUAL_KEYBOARD_THAI_LOWERCASE;
        break;
    case GUI_VIRTUAL_KEYBOARD_HINDI:
        lang = GUI_VIRTUAL_KEYBOARD_HINDI_UPPERCASE;
        break;
    case GUI_VIRTUAL_KEYBOARD_BENGALI:
        lang = GUI_VIRTUAL_KEYBOARD_BENGALI_LOWERCASE;
        break;
    case GUI_VIRTUAL_KEYBOARD_ARABIC:
        lang = GUI_VIRTUAL_KEYBOARD_ARABIC_LOWERCASE;
        break;
    }

    if(GUI_VIRTUAL_KEYBOARD_MAX_LANG == lang)
        return;

    layout = wgui_virtual_keyboard_get_vk_layout(lang, MMI_VK_TYPE_QWERTY);
    wgui_virtual_keyboard_get_keymapping_from_layout(layout, key_mapping + length, len - length);

}


